import React, { Component } from 'react';
import {Table, Button, Modal, ModalHeader, ModalBody, ModalFooter, FormGroup, Label, Input, CustomInput } from 'reactstrap';

class App extends Component {
  state = {
    burgers: [],
    newBurgerData:{
      name:'',
      ingredients: [],

    },
    newBurgerModal: false
  }

  componentWillMount(){

  }

  toggleNewBurgerModal(){
    this.setState({
      newBurgerModal: !this.state.newBurgerModal,
    })  
  }

  handleChange = e => {
    this.setState(this.newBurgerData.name = e.target.value);
  }

  render(){
    return (

      <div className="App container">

      <Button color="primary" onClick={this.toggleNewBurgerModal.bind(this)}>Add New Burger</Button>
      <Modal isOpen={this.state.newBurgerModal} 
              toggle={this.toggleNewBurgerModal.bind(this)} className={this.props.className}>
        <ModalHeader toggle={this.toggleNewBurgerModal.bind(this)}>Create New Burger</ModalHeader>
        <ModalBody>
          <FormGroup>
            <Label for="name">Burger name</Label>
            <Input id="name" handleChange ={this.handleChange}/>
          </FormGroup>
          <FormGroup>
          <Label for="ingredientsCheckbox">Ingredients</Label>
          <div>
            <CustomInput type="checkbox" id="ingredientCheckbox" label="Ketchup" />
            <CustomInput type="checkbox" id="ingredientCheckbox2" label="Mayo" />
            <CustomInput type="checkbox" id="ingredientCheckbox3" label="Se agotÃ³" disabled />
          </div>
        </FormGroup>
        </ModalBody>
        <ModalFooter>
          <Button color="primary" onClick={this.toggleNewBurgerModal.bind(this)}>Add Burger</Button>{' '}
          <Button color="secondary" onClick={this.toggleNewBurgerModal.bind(this)}>Cancel</Button>
        </ModalFooter>
      </Modal>




        <Table> 
          <thead>
            <tr>
            <th>#</th>
            <th>Name</th>
            <th>Description</th>
            <th>Image</th>

            </tr>
          </thead>

          <tbody>
            <tr>
            <td>1</td>
            <td>Burger name</td>
            <td>Delicious burger</td>
            <td>
            <Button color ="success" size= "sm" className="mr-2">Edit</Button>
            <Button color ="danger" size= "sm">Delete</Button>
            </td>
            </tr>
          </tbody>
        </Table>

      </div>
    );
  }
}


export default App;
